ListDeletedTickets.request_vtl:
#set($sort_by = $input.params("sort_by"))
#set($sort_order = $input.params("sort_order"))
{
  "query": "query MyQuery {
  ListDeletedTickets(sort_by: $sort_by,
    sort_order: $sort_order) {
    deleted_tickets {
actor {
id,
    name

  },
  deleted_at,
  id,
  previous_state,
  subject
},
count,
next_page,
previous_page
  }
}",
  "variables": null,
  "operationName": "MyQuery"
}

